+// -------------------------- Painter --------------------------
+class StaticWavePainter extends CustomPainter {
+  @override
+  void paint(Canvas canvas, Size size) {
+    final Paint bluePaint = Paint()
+      ..color = const Color(0xFF9196FF)
+      ..strokeWidth = 2
+      ..style = PaintingStyle.stroke;
+
+    final Paint grayPaint = Paint()
+      ..color = const Color(0xFF2A2A2A)
+      ..strokeWidth = 2
+      ..style = PaintingStyle.stroke;
+
+    const amplitude = 4.0;
+    const wavelength = 24.0;
+    const fillFraction = 1 / 3; // 1/3 blue, 2/3 gray
+
+    final double splitX = size.width * fillFraction;
+
+    // Path for blue section (1/3)
+    final Path bluePath = Path();
+    for (double x = 0; x <= splitX; x++) {
+      final y = math.sin((x / wavelength) * 2 * math.pi) * amplitude + size.height / 2;
+      if (x == 0) {
+        bluePath.moveTo(x, y);
+      } else {
+        bluePath.lineTo(x, y);
+      }
+    }
+    canvas.drawPath(bluePath, bluePaint);
+
+    // Path for gray section (remaining)
+    final Path grayPath = Path();
+    for (double x = splitX; x <= size.width; x++) {
+      final y = math.sin((x / wavelength) * 2 * math.pi) * amplitude + size.height / 2;
+      if (x == splitX) {
+        grayPath.moveTo(x, y);
+      } else {
+        grayPath.lineTo(x, y);
+      }
+    }
+    canvas.drawPath(grayPath, grayPaint);
+  }
+
+  @override
+  bool shouldRepaint(covariant CustomPainter oldDelegate) => false;
+}

