import 'package:flutter/material.dart';
import 'package:flutter_riverpod/flutter_riverpod.dart';
import 'package:hotspots_hostes/models/experience.dart';
import 'package:hotspots_hostes/providers/experience_provider.dart';
import 'package:hotspots_hostes/screens/onboarding_question_screen.dart';

class ExperienceSelectionScreen extends ConsumerStatefulWidget {
  const ExperienceSelectionScreen({super.key});

  @override
  ConsumerState<ExperienceSelectionScreen> createState() =>
      _ExperienceSelectionScreenState();
}

class _ExperienceSelectionScreenState
    extends ConsumerState<ExperienceSelectionScreen> with TickerProviderStateMixin {
  final TextEditingController _descriptionController = TextEditingController();

  // For card animation
  late List<AnimationController> _controllers;
  late List<Animation<double>> _scaleAnimations;
  late List<Animation<double>> _rotationAnimations;

  @override
  void initState() {
    super.initState();
  }

  @override
  void dispose() {
    _descriptionController.dispose();
    // Dispose animation controllers if they exist
    try {
      for (var controller in _controllers) {
        controller.dispose();
      }
    } catch (e) {
      // Ignore disposal errors
    }
    super.dispose();
  }

  void _toggleExperienceSelection(int experienceId, int index, List<Experience> experiences) {
    // Initialize animation controllers if not already done
    if (_controllers.length != experiences.length) {
      _initializeAnimations(experiences.length);
    }

    // Play animation for the tapped card
    if (index < _controllers.length) {
      _controllers[index].forward().then((_) {
        _controllers[index].reverse();
      });
    }

    final selectedExperiences = ref
        .read(selectedExperiencesProvider.notifier)
        .state;
    if (selectedExperiences.contains(experienceId)) {
      selectedExperiences.remove(experienceId);
    } else {
      selectedExperiences.add(experienceId);
    }
    ref.read(selectedExperiencesProvider.notifier).state = selectedExperiences;
  }

  void _initializeAnimations(int length) {
    // Dispose existing controllers
    try {
      for (var controller in _controllers) {
        controller.dispose();
      }
    } catch (e) {
      // Ignore disposal errors
    }

    _controllers = List.generate(length, (index) {
      return AnimationController(
        duration: const Duration(milliseconds: 200),
        vsync: this,
      );
    });

    _scaleAnimations = List.generate(length, (index) {
      return Tween<double>(begin: 1.0, end: 1.05).animate(
        CurvedAnimation(parent: _controllers[index], curve: Curves.easeInOut),
      );
    });

    _rotationAnimations = List.generate(length, (index) {
      return Tween<double>(begin: 0.0, end: index.isEven ? 0.02 : -0.02).animate(
        CurvedAnimation(parent: _controllers[index], curve: Curves.easeInOut),
      );
    });
  }

  void _navigateToNextScreen() {
    final selectedExperiences = ref.read(selectedExperiencesProvider);
    final description = _descriptionController.text;

    // Log the state
    print('Selected Experience IDs: $selectedExperiences');
    print('Description: $description');

    // Navigate to next screen
    Navigator.push(
      context,
      MaterialPageRoute(
        builder: (context) => OnboardingQuestionScreen(
          selectedExperienceIds: selectedExperiences.toList(),
          experienceDescription: description,
        ),
      ),
    );
  }

  // Mock data for experiences in case API is not available
  List<Experience> getMockExperiences() {
    return [
      Experience(
        id: 1,
        name: "Music Night",
        tagline: "Live music experiences",
        description: "Host live music events with local artists",
        imageUrl:
            "https://images.unsplash.com/photo-1470225620780-dba8ba36b745?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=1170&q=80",
        iconUrl: "https://cdn-icons-png.flaticon.com/512/61/61205.png",
      ),
      Experience(
        id: 2,
        name: "Food Festival",
        tagline: "Culinary adventures",
        description: "Organize food tasting events and culinary workshops",
        imageUrl:
            "https://images.unsplash.com/photo-1490818387583-1baba5e638af?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=1170&q=80",
        iconUrl: "https://cdn-icons-png.flaticon.com/512/61/61205.png",
      ),
      Experience(
        id: 3,
        name: "Art Workshop",
        tagline: "Creative expression",
        description: "Facilitate art workshops and creative sessions",
        imageUrl:
            "https://images.unsplash.com/photo-1579783902614-a3fb3927b6a5?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=1170&q=80",
        iconUrl: "https://cdn-icons-png.flaticon.com/512/61/61205.png",
      ),
      Experience(
        id: 4,
        name: "Game Tournament",
        tagline: "Competitive fun",
        description: "Organize gaming tournaments and esports events",
        imageUrl:
            "https://images.unsplash.com/photo-1550745165-9bc0b252726f?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=1170&q=80",
        iconUrl: "https://cdn-icons-png.flaticon.com/512/61/61205.png",
      ),
      Experience(
        id: 5,
        name: "Book Club",
        tagline: "Literary discussions",
        description: "Host book reading and discussion sessions",
        imageUrl:
            "https://images.unsplash.com/photo-1507842217343-583bb7270b66?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=1153&q=80",
        iconUrl: "https://cdn-icons-png.flaticon.com/512/61/61205.png",
      ),
      Experience(
        id: 6,
        name: "Fitness Class",
        tagline: "Health & wellness",
        description: "Lead fitness and wellness workshops",
        imageUrl:
            "https://images.unsplash.com/photo-1534438327276-14e5300c3a48?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=1170&q=80",
        iconUrl: "https://cdn-icons-png.flaticon.com/512/61/61205.png",
      ),
    ];
  }

  @override
  Widget build(BuildContext context) {
    final experiencesAsync = ref.watch(experiencesProvider);

    return Scaffold(
      appBar: AppBar(title: const Text('Select Your Experiences')),
      body: Padding(
        padding: const EdgeInsets.all(16.0),
        child: Column(
          crossAxisAlignment: CrossAxisAlignment.start,
          children: [
            const Text(
              'Choose the experiences you want to host',
              style: TextStyle(
                fontFamily: 'Space Grotesk',
                fontSize: 24, // Heading/H2-bold
                fontWeight: FontWeight.bold,
                color: Color(0xFFFFFFFF),
                height: 1.25, // 30px line height / 24px font size
                letterSpacing: -0.48, // -2% of 24px
              ),
            ),
            const SizedBox(height: 16),
            Expanded(
              child: experiencesAsync.when(
                data: (experiences) {
                  // Initialize animation controllers if needed
                  if (_controllers.length != experiences.length) {
                    _initializeAnimations(experiences.length);
                  }

                  // If we get data from API, use it
                  final selectedExperiences = ref.watch(
                    selectedExperiencesProvider,
                  );

                  return ListView.builder(
                    itemCount: experiences.length,
                    itemBuilder: (context, index) {
                      final experience = experiences[index];
                      final isSelected = selectedExperiences.contains(
                        experience.id,
                      );

                      // Create card with animation
                      return _buildExperienceCard(
                        experience: experience,
                        isSelected: isSelected,
                        index: index,
                        experiences: experiences,
                      );
                    },
                  );
                },
                loading: () => const Center(
                  child: CircularProgressIndicator(
                    valueColor: AlwaysStoppedAnimation<Color>(
                      Color(0xFF9196FF),
                    ), // primaryAccent
                  ),
                ),
                error: (error, stack) {
                  // If there's an error with the API, use mock data
                  print('Error loading experiences: $error');
                  final selectedExperiences = ref.watch(
                    selectedExperiencesProvider,
                  );
                  final mockExperiences = getMockExperiences();

                  // Initialize animation controllers if needed
                  if (_controllers.length != mockExperiences.length) {
                    _initializeAnimations(mockExperiences.length);
                  }

                  return ListView.builder(
                    itemCount: mockExperiences.length,
                    itemBuilder: (context, index) {
                      final experience = mockExperiences[index];
                      final isSelected = selectedExperiences.contains(
                        experience.id,
                      );

                      // Create card with animation
                      return _buildExperienceCard(
                        experience: experience,
                        isSelected: isSelected,
                        index: index,
                        experiences: mockExperiences,
                      );
                    },
                  );
                },
              ),
            ),
            const SizedBox(height: 16),
            const Text(
              'Describe your experience (Optional)',
              style: TextStyle(
                fontFamily: 'Space Grotesk',
                fontSize: 20, // Heading/H3-bold
                fontWeight: FontWeight.bold,
                color: Color(0xFFFFFFFF),
                height: 1.3, // 26px line height / 20px font size
                letterSpacing: -0.2, // -1% of 20px
              ),
            ),
            const SizedBox(height: 8),
            TextField(
              controller: _descriptionController,
              maxLines: 3,
              maxLength: 250,
              style: const TextStyle(
                fontFamily: 'Space Grotesk',
                fontSize: 16, // Body/B1-regular
                fontWeight: FontWeight.normal,
                color: Color(0xFFFFFFFF),
                height: 1.5, // 24px line height / 16px font size
                letterSpacing: 0,
              ),
              decoration: InputDecoration(
                hintText: 'Tell us about your experience...',
                hintStyle: const TextStyle(
                  fontFamily: 'Space Grotesk',
                  fontSize: 16, // Body/B1-regular
                  fontWeight: FontWeight.normal,
                  color: Color(0xFFFFFFFF),
                  height: 1.5, // 24px line height / 16px font size
                  letterSpacing: 0,
                ),
                border: OutlineInputBorder(
                  borderRadius: BorderRadius.circular(8),
                  borderSide: BorderSide(
                    color: Color(0xFFFFFF).withOpacity(0.16),
                  ), // border2
                ),
                focusedBorder: OutlineInputBorder(
                  borderRadius: BorderRadius.circular(8),
                  borderSide: const BorderSide(
                    color: Color(0xFF9196FF),
                  ), // primaryAccent
                ),
                filled: true,
                fillColor: const Color(0xFF151515), // base2
                counterStyle: const TextStyle(
                  fontFamily: 'Space Grotesk',
                  fontSize: 12, // Subtext/S1-regular
                  fontWeight: FontWeight.normal,
                  color: Color(0xFFFFFFFF),
                  height: 1.33, // 16px line height / 12px font size
                  letterSpacing: 0,
                ),
              ),
            ),
            const SizedBox(height: 16),
            SizedBox(
              width: double.infinity,
              child: ElevatedButton(
                onPressed: _navigateToNextScreen,
                style: ElevatedButton.styleFrom(
                  padding: const EdgeInsets.symmetric(vertical: 16),
                  shape: RoundedRectangleBorder(
                    borderRadius: BorderRadius.circular(12),
                  ),
                  backgroundColor: const Color(0xFF9196FF), // primaryAccent
                ),
                child: const Text(
                  'Next',
                  style: TextStyle(
                    fontFamily: 'Space Grotesk',
                    fontSize: 16, // Body/B1-bold
                    fontWeight: FontWeight.bold,
                    color: Color(0xFFFFFFFF),
                    height: 1.5, // 24px line height / 16px font size
                    letterSpacing: 0,
                  ),
                ),
              ),
            ),
          ],
        ),
      ),
    );
  }

  Widget _buildExperienceCard({
    required Experience experience,
    required bool isSelected,
    required int index,
    required List<Experience> experiences,
  }) {
    // Default animations if not initialized
    late Animation<double> scaleAnimation;
    late Animation<double> rotationAnimation;

    if (index < _scaleAnimations.length) {
      scaleAnimation = _scaleAnimations[index];
      rotationAnimation = _rotationAnimations[index];
    } else {
      scaleAnimation = Tween<double>(begin: 1.0, end: 1.0).animate(
        AlwaysStoppedAnimation(1.0),
      );
      rotationAnimation = Tween<double>(begin: 0.0, end: 0.0).animate(
        AlwaysStoppedAnimation(1.0),
      );
    }

    return AnimatedBuilder(
      animation: scaleAnimation,
      builder: (context, child) {
        return Transform.scale(
          scale: scaleAnimation.value,
          child: Transform.rotate(
            angle: rotationAnimation.value,
            child: Container(
              margin: const EdgeInsets.only(bottom: 16),
              decoration: BoxDecoration(
                borderRadius: BorderRadius.circular(12),
                image: DecorationImage(
                  image: NetworkImage(experience.imageUrl),
                  fit: BoxFit.cover,
                  colorFilter: isSelected
                      ? null
                      : const ColorFilter.mode(
                          Colors.black54,
                          BlendMode.darken,
                        ),
                ),
                boxShadow: [
                  BoxShadow(
                    color: Colors.black.withOpacity(0.3),
                    blurRadius: 8,
                    offset: const Offset(0, 4),
                  ),
                ],
              ),
              child: Container(
                decoration: BoxDecoration(
                  borderRadius: BorderRadius.circular(12),
                  gradient: LinearGradient(
                    begin: Alignment.topCenter,
                    end: Alignment.bottomCenter,
                    colors: [
                      Colors.transparent,
                      const Color(0xFF101010).withOpacity(0.9), // base1 with 90% opacity
                    ],
                  ),
                ),
                padding: const EdgeInsets.all(16),
                child: GestureDetector(
                  onTap: () => _toggleExperienceSelection(experience.id, index, experiences),
                  child: Row(
                    children: [
                      // Experience icon
                      Container(
                        width: 60,
                        height: 60,
                        decoration: BoxDecoration(
                          shape: BoxShape.circle,
                          color: isSelected
                              ? const Color(0xFF9196FF) // primaryAccent
                              : const Color(0xFF101010).withOpacity(0.7), // base1 with 70% opacity
                        ),
                        child: Icon(
                          isSelected ? Icons.check : Icons.add,
                          color: Colors.white,
                        ),
                      ),
                      const SizedBox(width: 16),
                      // Experience details
                      Expanded(
                        child: Column(
                          crossAxisAlignment: CrossAxisAlignment.start,
                          children: [
                            Text(
                              experience.name,
                              style: const TextStyle(
                                fontFamily: 'Space Grotesk',
                                fontSize: 20, // Heading/H3-bold
                                fontWeight: FontWeight.bold,
                                color: Color(0xFFFFFFFF),
                                height: 1.3, // 26px line height / 20px font size
                                letterSpacing: -0.2, // -1% of 20px
                              ),
                            ),
                            const SizedBox(height: 4),
                            Text(
                              experience.tagline,
                              style: const TextStyle(
                                fontFamily: 'Space Grotesk',
                                fontSize: 14, // Body/B2-regular
                                fontWeight: FontWeight.normal,
                                color: Color(0xFFFFFFFF),
                                height: 1.42, // 20px line height / 14px font size
                                letterSpacing: 0,
                              ),
                            ),
                            const SizedBox(height: 8),
                            Text(
                              experience.description,
                              style: const TextStyle(
                                fontFamily: 'Space Grotesk',
                                fontSize: 14, // Body/B2-regular
                                fontWeight: FontWeight.normal,
                                color: Color(0xFFFFFFFF),
                                height: 1.42, // 20px line height / 14px font size
                                letterSpacing: 0,
                              ),
                              maxLines: 2,
                              overflow: TextOverflow.ellipsis,
                            ),
                          ],
                        ),
                      ),
                      // Selection indicator
                      Container(
                        padding: const EdgeInsets.symmetric(
                          horizontal: 12,
                          vertical: 6,
                        ),
                        decoration: BoxDecoration(
                          color: isSelected
                              ? const Color(0xFF9196FF) // primaryAccent
                              : const Color(0xFFFFFF).withOpacity(0.05), // surfaceWhite2
                          borderRadius: BorderRadius.circular(20),
                        ),
                        child: Text(
                          isSelected ? 'Selected' : 'Select',
                          style: const TextStyle(
                            fontFamily: 'Space Grotesk',
                            fontSize: 14, // Body/B2-bold
                            fontWeight: FontWeight.bold,
                            color: Color(0xFFFFFFFF),
                            height: 1.42, // 20px line height / 14px font size
                            letterSpacing: 0,
                          ),
                        ),
                      ),
                    ],
                  ),
                ),
              ),
            ),
          ),
        );
      },
    );
  }
}
