+class ZigzagPainter extends CustomPainter {
+  @override
+  void paint(Canvas canvas, Size size) {
+    final Paint paint = Paint()
+      ..color = Colors.white.withOpacity(0.04)
+      ..strokeWidth = .1
+      ..style = PaintingStyle.stroke;
+
+    const double stepX = 40;
+    const double stepY = 16;
+
+    // Move the origin to the center so rotation looks clean
+    canvas.save();
+    canvas.translate(size.width / 2, size.height / 2);
+
+    // Rotate the canvas by 45 degrees (π/4 radians)
+    canvas.rotate(-math.pi / 4);
+
+    // Move back so the pattern covers the full visible area
+    canvas.translate(-size.width / 1.5, -size.height / 1.5);
+
+    // Draw diagonal zigzag lines
+    for (double y = 0; y < size.height * 2; y += stepY * 3) {
+      final Path path = Path();
+      path.moveTo(0, y + stepY);
+      for (double x = 0; x < size.width * 2; x += stepX) {
+        path.lineTo(x + stepX / 2, y);
+        path.lineTo(x + stepX, y + stepY);
+      }
+      canvas.drawPath(path, paint);
+    }
+
+    canvas.restore();
+  }
+
+  @override
+  bool shouldRepaint(CustomPainter oldDelegate) => false;
+}

